class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector <int> maxLims(k+1,0);
        vector<int> freqs(k+1,0);
        int n = nums.size();
        for(int i=0;i<(n/2);i++){
            int a = nums[i], b = nums[n - i - 1];
            int can = max(k-a, k-b);
            can  = max(can, a); can = max(can, b);
            freqs[abs(a-b)]++;
            maxLims[can]++;
        }
        int sum = 0;
        // converting maxLims to psums
        for(int i=0;i<=k;i++){
            sum += maxLims[i];
            maxLims[i] = sum;
        }
        int ans = INT_MAX;
        for(int i=0;i<=k;i++){
            int doubles = ((i) ? maxLims[i-1] : 0);
            int singles = (n/2) - doubles - freqs[i];
            ans = min(ans, singles + 2*doubles);
        }
        return ans;
    }
};
