class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        cnt = defaultdict(int)
        a = [0] * (k + 1)
        n = len(nums)
        for i in range(len(nums) // 2):
            cnt[abs(nums[i] - nums[n - i - 1])] += 1
            x, y = nums[i], nums[n - i - 1]
            if x > y:
                y, x = x, y
            a[max(k - x, y)] += 1

        res = inf
        for i in reversed(range(k + 1)):
            if i < k:
                a[i] += a[i + 1]
            res = min(res, n - a[i] - cnt[i])

        # print(cnt)
        # print(a)

        return res
