public class Solution {
    public int MinChanges(int[] nums, int k) {
        int[] arr = new int[k + 1];
        for(int i = 0, j = nums.Length - 1; i < j; ++i, --j){
            int a = nums[i],
                b = nums[j],
                c = Math.Abs(b - a),
                d = Math.Max(Math.Max(a, k - a), Math.Max(b, k - b));
            if(c > 0){
                ++arr[0];
                --arr[c];
            }
            if(c + 1 <= k)
                ++arr[c + 1];
            if(d + 1 <= k)
                ++arr[d + 1];
        }
        // Console.Write(arr[0] + " ");
        for(int i = 1; i <= k; ++i){
            arr[i] += arr[i - 1];
            // Console.Write(arr[i] + " ");
        }
        return arr.Min();
    }
}
