class Solution {
public:
    vector<int> ones;
    int cnt[100001] = {};
    int minChanges(vector<int>& nums, int k) {
        if(!k) return 0;
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            if(nums[i]>nums[n-i-1]) swap(nums[i],nums[n-i-1]);
            int diff = nums[n-i-1]-nums[i];
            int res = max(k-nums[i], nums[n-i-1]);
            cnt[diff]++;
            ones.push_back(res);
        }
        int ans = 1e9, m = n/2;
        sort(ones.begin(),ones.end());
        for(int i=0;i<=k;i++){
            int res = -cnt[i];
            int r1 = lower_bound(ones.begin(),ones.end(),i)-ones.begin();
            res += m-r1;
            res += r1*2;
            ans = min(ans, res);
        }
        return ans;
    }
};
