class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        lst = [0]*(k+1)
        for i in range(len(nums)//2):
            lst[0] = lst[0] + 1
            x = abs(nums[i] - nums[-i-1])
            lst[x] = lst[x] - 1
            if x + 1 < k+1:
                lst[x+1] = lst[x+1] + 1
            b = min(min(nums[i], nums[-i-1]), k - max(nums[i], nums[-i-1]))
            if b == 0:
                continue
            lst[k - b + 1] = lst[k - b + 1] + 1
        lst = list(accumulate(lst))
        return min(lst)
