class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> cnt(k + 1, 0);
        vector<int> dist(k + 1, 0);
        for (int i = 0; i < n / 2; ++i) {
            int left = nums[i];
            int right = nums[n - 1 - i];
            if (left > right) {
                swap(left, right);
            }
            cnt[abs(right - left)]++;
            int d = min(left, k - right);
            dist[k - d]++;
        }
        for (int i = 1; i <= k; ++i) {
            dist[i] += dist[i - 1];
        }
        int ret = n / 2 - cnt[0];
        for (int i = 1; i <= k; ++i) {
            ret = min(ret, n / 2 - cnt[i] + dist[i - 1]);
        }
        return ret;
    }
};
