class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mp;
        mp[0]=0;
        for(int i=0;i<n/2;i++)
            mp[abs(nums[i]-nums[n-1-i])]++;
        vector<pair<int,int>> vec;
        for(auto it:mp)
            vec.push_back({it.second,it.first});
        sort(vec.rbegin(),vec.rend());
        int ans=n/2;
        ans-=mp[0];
        for(auto it:vec)
        {
            int p=it.first;
            int x=it.second;
            if(n/2-p>ans)
            break;
            int cnt=0;
            for(int i=0;i<n/2;i++)
            {
                if(abs(nums[i]-nums[n-1-i])==x)
                continue;
                if(abs(nums[i]-nums[n-1-i])>x)
                cnt+=1;
                else if(abs(nums[i]-nums[n-1-i])+max(k-max(nums[i],nums[n-1-i]),min(nums[i],nums[n-1-i]))>=x)
                cnt+=1;
                else
                cnt+=2;
            }
            ans=min(ans,cnt);
        }
        return ans;
    }
};
