class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>pre(k+2,0);
        int n = nums.size();
        int i = 0 , j = n - 1;
        while(i<=j){
            int tdiff = abs(nums[i]-nums[j]);
            int maxdiff = max({k-nums[i],nums[i],k-nums[j],nums[j]});
            pre[0]++;
            pre[tdiff]--;
            pre[tdiff+1]++;
            pre[maxdiff+1]++;
            i++;
            j--;
        }
        int ans = pre[0];
        for(int i = 1 ; i <= k ; i++){
            pre[i] += pre[i-1];
            ans = min(ans,pre[i]);
        }
        return ans;
    }
};
