class Solution {
    public int minChanges(int[] nums, int k) {
        int ans=Integer.MAX_VALUE;
        HashMap<Integer,Integer> hm = new HashMap<>();
        int n = nums.length;
        for(int i=0;i<n/2;i++){
            int val = Math.abs(nums[i]-nums[n-i-1]);
            hm.put(val, hm.getOrDefault(val,0)+1);
        }

        PriorityQueue<int[]> pq = new PriorityQueue<>((a,b)->a[0]<b[0]?1:a[0]>b[0]?-1:a[1]-b[1]);
        for(int key : hm.keySet()){
            pq.offer(new int[]{hm.get(key),key});
        }
        int v=0;
        while(!pq.isEmpty() && v++<=3){
            int res=0;
            int a[] = pq.poll();
            for(int i=0;i<n/2;i++){
                if(Math.abs(nums[i]-nums[n-i-1])!=a[1]){
                    int val1 = Math.min(nums[i],nums[n-i-1]);
                    int val2 = Math.max(nums[i],nums[n-i-1]);
                    int curr = Math.abs(nums[i]-nums[n-i-1]);
                    if(curr>a[1]){
                        res+=1;
                    }
                    else{
                        int rem = a[1]-curr;
                        if(val2+rem>k && val1-rem<0) res+=2;
                        else res+=1;
                    }
                }
            }
            ans = Math.min(ans,res);
        }
        return ans;
    }
}
