class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();
        unordered_map<int,int> freq;
        vector<int> range(k+1);
        for(int i = 0; i<n/2; i++){
            freq[abs(nums[i]-nums[n-i-1])]++;
            int rng = max(max(nums[i],nums[n-i-1]),k-min(nums[i],nums[n-i-1]));
            range[rng]++ ;
        }

        for(int i = k-1; i>=0; i--){
            range[i] += range[i+1];
            // cout<<range[i]<<" ";
        }
        int ans = INT_MAX;
        for(auto it:freq){

            int X = it.first;
            // cout<<X<<" "<<it.second<<endl;
            // cout<<X<<" "<<range[X]<<" "<<it.second<<endl;
            int cnt = range[X] + (n/2-range[X])*2 - it.second;

            ans = min(cnt,ans);
        }
        return ans;

    }
};
