class Solution {
    int abs(int a){
        return a<0?-a:a;
    }
    int max(int a,int b){
        return a>b?a:b;
    }
    int min(int a,int b){
        return a<b?a:b;
    }
    public int minChanges(int[] nums, int k) {
        int n=nums.length;
        int[] cou=new int[k+1];
        int[] cou2=new int[k+1];
        for (int i=0;i<n/2;i++){
            cou[abs(nums[i]-nums[n-i-1])]++;
            int t=max(max(max(k-nums[i],k-nums[n-i-1]),nums[i]),nums[n-i-1]);
            cou2[t]++;
        }
        int max=0;
        int[] cou3=new int[k+1];
        for (int i=1;i<=k;i++){
            cou3[i]=cou2[i-1]+cou3[i-1];
        }
        int ret=n;
        for (int i=0;i<=k;i++){
            ret=min((n/2)-cou[i]+cou3[i],ret);
        }
        return ret;
    }
}
