class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> count;
        for(int i = 0;i < n / 2;i++){
            count[abs(nums[i] - nums[n - 1 - i])]++;
        }
        int maxVal = 0;
        int maxPos = -1;
        for(auto it = count.begin();it != count.end();it++){
            if(it->second > maxVal || (it->second == maxVal && maxPos > it->first)){
                maxVal = it->second;
                maxPos = it->first;
            } 
        }
        if(maxPos <= k / 2){
            return n / 2 - maxVal;
        }
        map<int, int> anoC;
        anoC[maxVal] = maxPos;
        for(auto it = count.begin();it != count.end();it++){
            if(it->second != maxVal){
                if(it->first <= k / 2){
                    if(anoC.find(0) == anoC.end() || it->second > anoC[0]){
                        anoC[0] = it->second;
                    }
                }
                else{
                    if(anoC.find(it->second) == anoC.end() || anoC[it->second] > it->first){
                        anoC[it->second] = it->first;
                    }
                }
            }
        }
        int res = 2 * n;
        auto it = anoC.begin();
        if(it->first == 0){
            res = n / 2 - it->second;
            it++;
        }
        while(it != anoC.end()){
            int temp = 0;
            for(int i = 0;i < n / 2;i++){
                if(abs(nums[i] - nums[n - 1 - i]) != it->second){
                    int mi = min(nums[i], nums[n - 1 - i]);
                    int ma = max(nums[i], nums[n - 1 - i]);
                    if(ma >= it->second || mi <= k - it->second){
                        temp++;
                    }
                    else{
                        temp += 2;
                    }
                }
            }
            res = min(res, temp);
            it++;
        }
        return res;
    }
};
