bool compareBySecondDesc(const std::pair<int, int> &a, const std::pair<int, int> &b) {
    return a.second > b.second;
}

class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        vector<int> diff;
        int n=nums.size()/2;
        for(int i=0;i<n;i++){
           // cout<<abs(nums[i]-nums[n-i-1])<<endl;
            diff.push_back(abs(nums[i]-nums[2*n-i-1]));
        }

        unordered_map<int,int> freq;
        for(auto x:diff){
          //  cout<<x<<" ";
            freq[x]++;
        }
        //cout<<endl;
        int maxf = 0;
        int maxd = 0;
        vector<pair<int,int>>v;
        for(auto x: freq){
            v.push_back({x.first,x.second});
        }
        sort(v.begin(), v.end(), compareBySecondDesc);


        int ans=INT_MAX;
        //cout<<maxd<<endl;
        for(int j=0;j<20 && j<v.size();j++){
            maxd= v[j].first;
        int currAns=0;
        for(int i=0;i<n;i++){
            if(abs(nums[i]-nums[2*n-i-1])==maxd){
                continue;
            }
            int a = min(nums[i],nums[2*n-i-1]);
            int b = max(nums[i],nums[2*n-i-1]);
            //cout<<a<<" "<<b<<" "<<(b>=maxd || a+maxd <=k)<< endl;
            if( b>=maxd || a+maxd <=k)
                currAns++;
            else 
                currAns+=2;
            }

            ans=min(ans,currAns);
        }

//        cout<<n<<" "<<maxf;  
        return ans;
    }
};
