class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<pair<int,int>> a;
        int n = nums.size();
        unordered_map<int,int> mp1;
        for (int i=0; i<n/2; i++){
            a.push_back({nums[i],nums[n-i-1]});
            mp1[abs(nums[i] - nums[n-i-1])]++;
        }
        vector<int> maxi;
        for (auto it : a){
            int max2 = max(it.first,it.second);
            int min2 = min(it.first,it.second);
            maxi.push_back(max(k-min2,max2));
        }
        int m = maxi.size();
        sort(maxi.begin(),maxi.end());
        int ch = INT_MAX;
        for (auto it : mp1){
            auto it1 = lower_bound(maxi.begin(),maxi.end(),it.first)-maxi.begin();
            int cnt = (m-it1-it.second) + (it1)*2;
            ch = min(ch,cnt);
        }
        return min(ch,n);
    }
};
