class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
     map<int, vector<int>> diffToMaxX;
    vector<int> allX;
    int n = nums.size();

    for (int i = 0; i < n / 2; ++i) {
        int a = nums[i], b = nums[n - i - 1];
        int diff = abs(a - b);
        int X = max({a, b, k - a, k - b});
        diffToMaxX[diff].push_back(X);
        allX.push_back(X);
    }

    sort(allX.begin(), allX.end());
    int minChanges = n;

    for (auto& entry : diffToMaxX) {
        int diff = entry.first;
        auto& X_values = entry.second;

        int index = lower_bound(allX.begin(), allX.end(), diff) - allX.begin();
        int changes = index * 2 + (n / 2 - index);

        for (int X : X_values) {
            if (X < diff) {
                changes -= 2;
            } else {
                changes -= 1;
            }
        }
        minChanges = min(minChanges, changes);
    }

    return minChanges;
    }


};
