class Solution {
public:
    int minChanges(vector<int>& nums, int kk) {
        int n=nums.size();
        unordered_map<int,int> m;
        for(int i=0;i<n/2;++i){
            ++m[abs(nums[i]-nums[n-1-i])];
        }
        int res=n/2;
        vector<pair<int,int>> a;
        for(auto [k,v]:m){
            a.push_back({v,k});

        }
        sort(a.begin(),a.end(),greater<pair<int,int>>());
        for(auto [v,k]:a){
            if(n/2-v<res){
                int t=0;
                for(int i=0;i<n/2;++i){
                    if(abs(nums[i]-nums[n-1-i])!=k){
                        if(nums[i]-k>=0||nums[i]+k<=kk||nums[n-1-i]-k>=0||nums[n-1-i]+k<=kk){
                            ++t;
                        }
                        else{
                            t+=2;
                        }
                    }
                }
                // cout<<"v="<<v<<" t="<<t<<endl;
                res=min(res,t);
            }    
        }

        return res;
    }
};
