class Solution {
public:
    int minChanges(vector<int>& num, int k) {
        int n = num.size();
        set<int> st;
        map<int,int> mp;
        for(int i=0; i<n/2; i++){
            st.insert(abs(num[i] - num[n-i-1]));
            mp[abs(num[i] - num[n-i-1])]++;
        }
        if(st.size() == 1){
            return 0;
        }
        int eq = 0, val = INT_MIN;
        int eq1 = 0, val1 = INT_MIN;
        for(auto it:mp){
            if(it.second>val){
                val = it.second;
                eq = it.first;
            }
        }
        for(auto it:mp){
            if(it.second>val1 && it.first!=eq){
                val1 = it.second;
                eq1 = it.first;
            }
        }
        int ans = 0;
        for(int i=0; i<n/2; i++){
            if(abs(num[i] - num[n-i-1]) == eq) continue;
            int v = min(num[i], num[n-i-1]);
            int v1 = max(num[i], num[n-i-1]);
            v+=eq;
            v1-=eq;
            if(v>k && v1<0) ans+=2;
            else ans+=1;
        }
        int ans1 = 0;
        for(int i=0; i<n/2; i++){
            if(abs(num[i] - num[n-i-1]) == eq1) continue;
            int v = min(num[i], num[n-i-1]);
            int v1 = max(num[i], num[n-i-1]);
            v+=eq1;
            v1-=eq1;
            if(v>k && v1<0) ans1+=2;
            else ans1+=1;
        }


        return min(ans, ans1);

    }
};
