class Solution {
public:
    int find(int a,int b,int d,int k){
        int mi=min(a,b);
        int ma=max(a,b);
        if(d==abs(mi-ma)) return 0;
        else if(d<abs(mi-ma)) return 1;
        else if(k-mi>=d || ma>=d) return 1;
        return 2;
    }
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        map<int,int> mp;
        for(int i=0;i<n/2;i++){
            mp[abs(a[i]-a[n-i-1])]++;
        }
        map<int,int> all;
        for(auto it:mp){
            if(all.find(it.second)==all.end()) all[it.second]=it.first;
            else all[it.second]=min(all[it.second],it.first);
        }
        vector<int> mk;
        for(auto it:all){
            mk.push_back(it.second);
        }
        int ans=1e9;
        for(auto it:mk){
            int cur=0;
            for(int i=0;i<n/2;i++){
                cur+=find(a[i],a[n-i-1],it,k);
            }
            ans=min(ans,cur);
        }
        for(auto it:mk) cout<<it<<" ";
        cout<<endl;
        return ans;
    }
};
