class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        vector<int> h(nums.size()/2+1);
        h[0]=-1;
        int i;
        for(i=0;i<nums.size()/2;i++)
        {
            int tmp=abs(nums[i]-nums[nums.size()-1-i]);
            if(m.count(tmp))
            {
                m[tmp]+=1;
            } else
            {
                m[tmp]=1;
            }
            h[i+1]=max(max(nums[i],k-nums[i]),max(nums[nums.size()-1-i],k-nums[nums.size()-1-i]));
        }
        sort(h.begin(),h.end());
        int ans=nums.size();
        for(i=0;i<=k;i++)
        {
            int left=0,right=nums.size()/2,mid;
            while(left!=right)
            {
                mid=(left+right+1)/2;
                if(h[mid]<i)
                {
                    left=mid;
                } else
                {
                    right=mid-1;
                }
            }
            int t1=nums.size()/2-left;
            int t2=0;
            if(m.count(i))
            {
                t2=m[i];
            }
            ans=min(ans,t1-t2+2*((int)nums.size()/2-t1));
        }
        return ans;
    }
};
