class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> M;
        vector<int> ch1(k+1);
        for ( int i = 0; i < n/2; ++i ) {
            ch1[max(k - min(nums[i], nums[n-1-i]), max(nums[i], nums[n-1-i]))]++;
            M[abs(nums[i]-nums[n-1-i])]++;
        }
        for ( int i = k-1; i >= 0; --i) ch1[i] += ch1[i+1];
        int ans = n;
        for ( auto& [d,count] : M ) {
            if ( d < (k+1)/2 ) ans = min(ans, n/2-count);
            else ans = min(ans, n-count-ch1[d] );
        }

        return ans;
    }
};
