const int N=1e5+10;
class Solution {
public:
    int st[N];
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> mp;
        int n=nums.size();
        for(int i=0;i<n/2;i++)
        {
            int a=nums[i],b=nums[n-1-i];
            if(a<b)
            swap(a,b);
            st[a-b]-=1;
            st[a-b+1]+=1;
            int tmp=max(a+1,k-b+1);
            st[tmp]+=1;
        }
        int res=n;
        int cur=0;
        for(int i=0;i<=k;i++)
        {
            cur+=st[i];
            res=min(res,cur);
        }
        return res+n/2;
    }
};
