const int N=1e5+10;
class Solution {
public:
    int a[N];
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int i=0,j=n-1;
        while(i<j){
            int mx=max(nums[i],nums[j]),mi=min(nums[i],nums[j]);
            int p=mx-mi;
            int mm=max(k-mi,mx);
            a[0]+=1,a[p]-=1;
            a[p+1]+=1,a[mm+1]-=1;
            a[mm+1]+=2,a[N-1]-=2;
            i++,j--;
        }
        int res=1e5;
        for(int i=1;i<N;i++) a[i]+=a[i-1];
        // for(int i=0;i<N;i++) cout<<a[i]<<' ';
        for(int i=0;i<=100000;i++){
            res=min(res,a[i]);
        }
        return res;
    }
};
