class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        map<int, int> mp;
        vector<int> st;
        for (int i=0; i<n/2; i++){
            mp[abs(a[i] - a[n-i-1])]++;
            st.push_back(max(max(a[i], a[n-i-1]) - 0 , k - min(a[i], a[n-i-1])));
        }
        int mmax = n;
        n/=2;
        sort(st.begin(), st.end());
        for (auto [x, y]:mp){
            mmax = min(mmax, n-y + (int)(lower_bound(st.begin(), st.end(), x) - st.begin() ));
        }
        return mmax;
    }
};
