class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        map<int,int> m;
        vector<int> v(k+2,n/2);
        for(int i=0;i<n/2;i++){
            int x=a[i];
            int y=a[n-i-1];
            int d=abs(x-y);
            m[d]++;
            int mini=min(x,y);
            int maxi=max(x,y);
            int num1=mini;
            int num2=k-maxi;
            int max1=max(num1,num2);
            v[max1+d+1]++;
        }
        // for(auto it:v){
        //     cout<<it<<" ";
        // }
        // cout<<"\n";
        // return 0;
        int ct=0;
        for(int i=0;i<=k;i++){
            int d=v[i]-n/2;
            v[i+1]+=d;
            v[i]-=m[i];
        }
        // for(auto it:v){
        //     cout<<it<<" ";
        // }
        // cout<<"\n";
        int ans=n;
        for(int i=0;i<=k;i++){
            ans=min(ans,v[i]);
        }
        return ans;
    }
};
