class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int>arr;
        for(int index=0;index<nums.size()/2;index++){
            int x=nums[index];
            int y=nums[nums.size()-index-1];
            int maxi=max({k-x,k-y,x,y});
            int d=abs(x-y);
            arr.push_back(maxi);
            mp[d].push_back(maxi);

        }
        int answer=nums.size();
        sort(arr.begin(),arr.end());
        for(auto &it:mp){
            int d=it.first;
            int find=lower_bound(arr.begin(),arr.end(),d)-arr.begin();
            find=find*2+(nums.size()/2-find);
            for(auto &i:it.second){
                if(i<d){
                    find-=2;
                }
                else{
                    find-=1;

                }
            }
            answer=min(answer,find);
        }
        return answer;
    }
};
