class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mm;
        int n = nums.size();
        int h = n/2;
        for(int i = 0;i < h;i++){
            int a = abs(nums[i] - nums[n-i-1]);
            int m1 = max(nums[i], k - nums[i]);
            int m2 = max(nums[n-i-1],k - nums[n-i-1]);
            mm[a].push_back(max(m1,m2));
        }
        int ans = n/2;
        vector<vector<int>> v;
        vector<int> count;
        for(auto p:mm){
            v.push_back(p.second);
            count.push_back(p.first);
        }
        int so = 0;
        map<int,int> m3;
        int cur = 0;
        for(int i = 0;i < v.size();i++){
            //printf("%d i %d v-size %d\n",i,v.size());
            so += v.size();
            int t1 = h - so;
            for(auto p:m3){
                if(p.first < count[i]){
                    cur++;
                }else{
                    break;
                }
            }
            while(m3.size() && m3.begin()->first < count[i]){
                m3.erase(m3.begin());
            }
            if(t1 + so - (int)v[i].size() + cur < ans){
                ans = t1 + so - (int)v[i].size() + cur;
                //printf("update ans = %d t1 = %d so = %d v-sz %d cur %d\n",t1 + so - (int)v[i].size() + cur,t1,so,v.size(),cur);
            }
            for(auto c:v[i]){
                m3[c]++;
            }

        }
        return ans;

    }
};
