class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int i,j,n=nums.size();
        map<int,int> mp;
        vector<int> v;
        for(i=0;i<n/2;i++){
            if(nums[n-i-1]<nums[i]){
                swap(nums[i],nums[n-i-1]);
            }
            mp[abs(nums[i]-nums[n-i-1])]++;
            v.push_back(max(nums[n-i-1],k-nums[i]));
           // cout<<v.back()<<" - ";
        }
       // cout<<endl;
        sort(v.begin(),v.end()); 
        int ans=n/2;
        for(auto it:mp){
            int res=n/2-it.second;
            j=lower_bound(v.begin(),v.end(),it.first)-v.begin();
            cout<<it.first<<" "<<j<<endl;
            res+=j;
            ans=min(ans,res);
        }


        return ans;
    }
};
