class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int,int>mp;int n=nums.size();int ans=n;
        vector<int> a(k+10,0);
        for(int i=0,j=nums.size()-1;i<j;i++,j--){
            int nik=abs(nums[i]-nums[j]);
            int maxi= max(max(nums[i],nums[j]),max(k-nums[i],k-nums[j]));
            int mini= 0;
            mp[nik+1 ]++;
            a[maxi+1  ]++;
            a[mini ]--;
        } int nik=0;
        for(int idx=k+9;idx>=0;idx--){
          // cout<<" "<<idx<<" "<<a[idx]<<" "<<mp[idx]<<endl;
            nik+=a[idx];
            int pik=nik+(2*((n/2) -nik)) -mp[idx ];
            ans=min(ans,pik);
        }return ans;
    }
};
