class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        vector<int> one(k+1,0);
        vector<int> cnt(k+1,0);
        for(int i=0,j=n-1;i<j;++i,--j){
            int x=a[i];
            int y=a[j];
            ++cnt[abs(x-y)];
            int res=max(max(x,k-x),max(y,k-y));
            ++one[res];
        }
        int ans=1e9;
        for(int i=k-1;i>=0;--i)
            one[i]+=one[i+1];
        for(int i=0;i<=k;++i){
            int tmp=n-cnt[i]-one[i];
            ans=min(ans,tmp);
        }
        return ans;
    }
};
