class Solution {
    class Pair
    {
        int diff, f;
        Pair(int a, int b)
        {
            diff = a;
            f = b;
        }
    }
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        Pair[] diffs = new Pair[k+1];
        for(int i = 0; i <= k; i++)
            diffs[i] = new Pair(i, 0);
        for(int i = 0; i < n/2; i++)
        {
            int d = Math.abs(nums[i] - nums[n-1-i]);
            diffs[d].f++;
        }
        int ans = Integer.MAX_VALUE;
        Arrays.sort(diffs, (a, b) -> b.f - a.f);
        for(int i = 0; i <= k; i++)
        {
            int temp = 0;
            for(int j = 0; j < n/2; j++)
            {
                int d = Math.abs(nums[j] - nums[n-1-j]);
                if(d == diffs[i].diff)
                    continue;
                int a = Math.max(nums[j], nums[n-1-j]);
                int b = Math.max(k-nums[j], k-nums[n-1-j]);
                int c = Math.max(a, b);
                if(diffs[i].diff <= c)
                    temp++;
                else
                    temp += 2;
            }
            if(temp > ans)
                break;
            ans = Math.min(ans, temp);
        }
        return ans;
    }
}
