class Solution {
    unordered_map<int,int> mp;
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        for(int i=0;i<n/2;i++)
        {
            mp[abs(a[i]-a[n-i-1])]++;
           // cout<<a[i]<<endl;
            //cout<<"iii "<<abs(a[i]-a[n-i-1])<<endl;
        }
        int ans=1e9;
        for(auto it:mp)
        {
            int x=it.second,y=it.first;
            int res=(n-x*2)/2,res1=0;
            //cout<<res<<endl;
            if(res>=ans)
                continue;
            for(int i=0;i<n/2;i++)
            {
                int t=abs(a[i]-a[n-i-1]);
                if(t!=y)
                {
                    int t1=max(a[i],a[n-i-1]),t2=min(a[i],a[n-i-1]);
                    if(t>y)
                    {
                        if(t1-(t-y)>=0||t2+(t-y)<=k)
                        {
                            res1+=1;
                        }
                        else
                        {
                            res1+=2;
                        }
                    }
                    else
                    {

                        if(t1+(y-t)<=k||t2-(y-t)>=0)
                        {
                            //cout<<"wxl"<<endl;
                            res1+=1;
                        }
                        else
                        {
                            //cout<<res1<<endl;
                            res1+=2;
                        }
                    }
                }
            }
            //if(res1==6)
                //cout<<y<<" "<<x<<endl;
            ans=min(ans,res1);
        }
        return ans;
    }
};
