class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> v;
        unordered_map<int,int> mp;
        for(int i=0;i<n/2;i++){
            v.push_back(max(max(nums[i],nums[n-i-1]),k-(min(nums[i],nums[n-i-1]))));
            mp[abs(nums[i]-nums[n-i-1])]++;
        }

        sort(v.begin(),v.end());
        int kk;
        int ans=INT_MAX;
        for(int i=0;i<=k;i++){
            int l=-1;
            int h=v.size();
            while(h-l>1){
                int m=l+(h-l)/2;
                if(v[m]<i){
                    l=m;
                }
                else{
                    h=m;
                }
            }
            int dd=(2*(l+1)+((v.size()-l-1))-mp[i]);
            if(dd<=ans){
                ans=dd;
                kk=i;
            }
        }
        return ans;
    }
};
