class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> dis(n/2,0);
        vector<int> mx(n/2,0);
        map<int,int> freq;
        for(int i=0;i<n/2;i++) dis[i] = abs(nums[i]-nums[n-i-1]);
        for(int i=0;i<n/2;i++) freq[dis[i]]++;
        for(int i=0;i<n/2;i++){
            mx[i] = max(abs(nums[i]-k),nums[i]);
            mx[i] = max(abs(nums[n-i-1]-k),mx[i]);
            mx[i] = max(nums[n-i-1],mx[i]);
        }
        sort(mx.begin(),mx.end());
        int ans = INT_MAX;
        for(auto& it : freq){
            int diff = it.first;
            int times = it.second;
            int twos = lower_bound(mx.begin(),mx.end(),diff) - mx.begin();
            int temp_ans = n/2-times;
            ans = min(ans,temp_ans+twos);
        }
        return ans;
    }
};
