class Solution {
    public function minChanges($nums, $k) {
        $m = array();
        $v = array();

        for ($i = 0; $i < count($nums) / 2; $i++) {
            $a = $nums[$i];
            $b = $nums[count($nums) - $i - 1];
            $diff = abs($a - $b);
            $X = max($a, $b, $k - $a, $k - $b);
            if (!isset($m[$diff])) {
                $m[$diff] = array();
            }
            $m[$diff][] = $X;
            $v[] = $X;
        }

        $ans = count($nums);
        sort($v);

        foreach ($m as $diff => $values) {
            $T = $this->lower_bound($v, $diff);
            $T = $T * 2 + (count($nums) / 2 - $T);

            foreach ($values as $X) {
                if ($X < $diff) {
                    $T -= 2;
                } else {
                    $T -= 1;
                }
            }

            $ans = min($ans, $T);
        }

        return $ans;
    }

    private function lower_bound($array, $value) {
        $low = 0;
        $high = count($array);

        while ($low < $high) {
            $mid = floor(($low + $high) / 2);
            if ($array[$mid] < $value) {
                $low = $mid + 1;
            } else {
                $high = $mid;
            }
        }

        return $low;
    }
}

// Example usage:
$solution = new Solution();
echo $solution->minChanges(array(1, 2, 3, 4, 5, 6), 3); // Replace with the desired input array and value of k
