class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        using pii = std::pair<int, int>;
        std::vector<pii> ps;
        std::map<int, int> diff_num;

        std::vector<int> adjustable(1e5+10);

        for (int i = 0, j = nums.size() - 1; i < j; i++, j--) {
            if (nums[i] > nums[j]) {
                std::swap(nums[i], nums[j]);
            }
            //ps.emplace_back(nums[i], nums[j]);
            diff_num[nums[j] - nums[i]]++;
            int adj = std::max(k - nums[i], nums[j] - 0);
            adjustable[adj]++;
        }

        int n = nums.size() / 2;
        for (auto it = adjustable.rbegin() + 1; it != adjustable.rend(); it++) {
            *it += *(it - 1);
        }
        int ans = nums.size();
        for (auto [diff, num] : diff_num) {
            int tmp = adjustable[diff] - num;
            if (tmp < n - num) {
                tmp += (n - num - tmp) * 2; 
            }
            ans = std::min(tmp, ans);
        }

        return ans;
    }
};
