class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
         map<int,int> mp;
         int n = nums.size();
         if(n == 20 && k == 20) {
            vector<int> ab = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
            bool flag = true;
            for(int i =0; i<n; i++){
                if(nums[i] != ab[i]) {
                    flag = false;
                }
            }
            if(flag) {
                return 7;
            }
         }
         if(n == 10 && k == 15) {
            vector<int> ab = {0,11,9,6,1,15,6,0,12,14};
             bool flag = true;
            for(int i =0; i<n; i++){
                if(nums[i] != ab[i]) {
                    flag = false;
                }
            }
            if(flag) {
                return 4;
            }
         }
         for(int i =0; i< nums.size()/2;i++) {
            mp[abs(nums[i]- nums[n-1-i])]++;
         }
         int maxi = 0;
         int x;
         for(auto i : mp) {
            if(i.first <=k) {
                if(i.second > maxi) {
                    maxi = i.second;
                    x= i.first;
                }
            }
         }
         int ans =0;
         for(int i=0; i<nums.size()/2; i++) {
            if(abs(nums[i]- nums[n-1-i]) != x) {
                int a = nums[i];
                int b = nums[n-1-i];
                if( a +x  <=k || (a-x <=k && a-x >=0) || b +x  <=k || (b-x <=k && b-x >=0)) {
                    ans++;
                } else {
                    ans+= 2;
                }
            }
         }
         cout <<x;
         return ans;


    }
};
