class Solution {
public:

    bool isPalindrome(string s){
        int n = s.size();
        for(int i=0; i<n/2; i++){
            if(s[i] != s[n-i-1]){
                return false;
            }
        }
        return true;
    }

    bool isPrime(int n){
        for(int i=2; i*i<=n; i++){
            if(n % i == 0){
                return false;
            }
        }
        return true;
    } 

    void checkAns(map<int, vector<int>> &mp, int &ans, vector<int> &v, int n){
        for(auto &it : mp){
            int val = it.ff;
            int lb = lower_bound(v.begin(),v.end(),val) - v.begin();
            lb = lb*2 + (n/2 - lb);

            for(auto &it1 : it.ss){
                if(it1 < val){
                    lb -= 2;
                }else{
                    lb -= 1;
                }
            }
            ans = min(ans, lb);
        }
    }

    int getAns(vector<int> &nums, int k){
        int n = nums.size();
        map<int,vector<int>> mp;
        vector<int> v;
        int i=0;
        while(i < n/2){
            int val1 = nums[i];
            int val2 = nums[n-i-1];
            int diff = abs(val1 - val2);
            int diff_x = max({val1, val2, k - val1, k - val2});
            mp[diff].pb(diff_x);
            v.pb(diff_x);
            i++;
        }
        int ans = n;
        sort(v.begin(),v.end());
        checkAns(mp, ans, v, n);

        return ans;
    }

    int minChanges(vector<int> &nums, int k) {
        int ans = getAns(nums, k);
        return ans;
    }
};

