class Solution:
    def minChanges(self, nums, k):
        differences = defaultdict(list)
        max_values = []

        for i in range(len(nums) // 2):
            num_start = nums[i]
            num_end = nums[len(nums) - i - 1]
            difference = abs(num_start - num_end)
            max_value = max(num_start, num_end, k - num_start, k - num_end)
            differences[difference].append(max_value)
            max_values.append(max_value)

        minimum_changes = len(nums)
        max_values.sort()

        for difference, value_list in differences.items():
            position = bisect_left(max_values, difference)
            total_changes = position * 2 + (len(nums) // 2 - position)

            for value in value_list:
                if value < difference:
                    total_changes -= 2
                else:
                    total_changes -= 1

            minimum_changes = min(minimum_changes, total_changes)

        return minimum_changes
