int cost[100010];

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        const int n = (int) size(nums);
        for (int i = 0; i <= k; i++) {
            cost[i] = 0;
        }

        cost[0] = n/2;
        for (int i = 0; i < n/2; i++) {
            int m1 = max(nums[i], k - nums[i]);
            int m2 = max(nums[n-1-i], k - nums[n-1-i]);
            cost[max(m1, m2) + 1]++;
        }
        for (int i = 1; i <= k; i++) {
            cost[i] += cost[i-1];
        }
        for (int i = 0; i < n/2; i++) {
            int d = abs(nums[i] - nums[n-1-i]);
            cost[d]--;
        }

        int ans = cost[0];
        for (int i = 1; i <= k; i++) {
            ans = min(ans, cost[i]);
        }

        return ans;
    }
};
