class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ops = nums.size();
        vector<int>occurence(k+5, 0);
        vector<int>arr;
        vector<int>operation(k+5, 0);
        unordered_map<int,vector<int>>indexMap;

        int n = nums.size();
        vector<vector<int>>vec;

        for(int i=0;i<n/2;i++){
            int j = n - i - 1;
            int diff = abs(nums[i] - nums[j]);
            int MAX = max({nums[i], nums[j], k - nums[i], k - nums[j]});
            arr.push_back(MAX);
            indexMap[diff].push_back(MAX);
            // vec.push_back({diff, nums[i], nums[j]});
        }

        sort(arr.begin(),arr.end());

        for(auto &itr : indexMap){
            int M = lower_bound(arr.begin(),arr.end(),itr.first) - arr.begin();
            int element = itr.first;

            M *= 2;
            M += ((n/2) - (M/2));

            for(int i=0;i<itr.second.size();i++){
                int ele = itr.second[i];
                if(ele < element) M -= 2;
                else M -= 1;
            }

            ops = min(ops, M);
        }

        return ops;

