class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans1 = 1e9 ;
        int n = nums.size() ;
        int i=0 , j =n-1 ;
        int maxi = 0 , req = 0 ;
        map<int,int>mp ;
        while(i<=j){
            mp[abs(nums[i]-nums[j])]++;
                i++ ;
                j-- ;
        }
        vector<vector<int>>tt;
        for(auto &it:mp){
            tt.push_back({it.second , it.first});
        }
        sort(tt.rbegin() , tt.rend()) ;

        for(int ii=0;ii<tt.size();ii++){
            int req = tt[ii][1] ;
            int ans =0 ;
           int  i = 0 , j = n-1 ;

        while(i<=j){
            if(abs(nums[i]-nums[j])==req)  {
                i++ ;
                j-- ;
                continue ;
            }
            else{
                int mn = min(nums[i] , nums[j]) ;
                int  mx = max(nums[i], nums[j]) ;
                if(mn+req <= k) ans++ ;
                else if((mx-req >= 0&& mx-req <= k)) ans++ ;
                else ans+=2 ;
                    i++ ;
                j-- ;
        }
        }
            if(ans <= ans1) ans1 = ans ;
            else break ;
        }

            return ans1 ;
    }
};
