class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int>mp;
        for(int i=0;i<n;i++){
            mp[nums[i]]++;
        }
        vector<int>v;
        map<int,vector<int>>mpv;
        for(int i=0;i<n/2;i++){
            int phla=nums[i];
            int baad=nums[n-i-1];
            int diff=abs(phla-baad);
            int maxi=max({phla,baad,k-phla,k-baad});
            mpv[diff].push_back(maxi);
            v.push_back(maxi);
        }
        sort(v.begin(),v.end());
        int ans=n;
        for(auto &it:mpv){
            int shuru=it.first;
            int low=lower_bound(v.begin(),v.end(),shuru)-v.begin();
            low=low+n/2;
            for(auto &maxi:it.second){
                if(shuru==maxi){
                    low=low-1;
                }
                else if(shuru>maxi){
                    low=low-2;
                }
                else{
                    low=low-1;
                }
            }
            ans = min(low,ans);
        }
        return ans;
    }
};
