class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector <int> scope;
        map <int, int> cnt;
        for (int i = 0; i < n / 2; i ++) {
            if (nums[i] > nums[n - i - 1]) {
                swap(nums[i], nums[n - i - 1]);
            }
            cnt[nums[n - i - 1] - nums[i]] ++;
            scope.push_back(max(k - nums[i], nums[n - i - 1] - 0));
        }
        sort(scope.begin(), scope.end());
        int ans = 1e5 + 5;
        for (int i = 0; i < n / 2; i ++) {
            int diff = nums[n - i - 1] - nums[i];
            int less = lower_bound(scope.begin(), scope.end(), diff) - scope.begin();
            int rest = n / 2 - less;
            ans = min(ans, 2 * less + rest - cnt[diff]); 
        }
        return ans;
    }
};
