class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int max_diff=k;

        int n=nums.size();

        map<int,int>mp;

        for(int i=0;i<n/2;i++){

            mp[abs(nums[i]-nums[n-1-i])]++;
        }

        int temp=0;
        int ans;

        vector<int>vec;
        vector<pair<int,int>>v;

        for(auto it:mp){

            if(it.first<=max_diff){

                vec.push_back(it.first);
                v.push_back({it.second,it.first});
            }

        }

        sort(v.begin(),v.end(),greater<pair<int,int>>());

      int val=INT_MAX;

      for(int i=0;i<vec.size();i++){

         if(val<=(n/2-(mp[v[i].second]))) break;

         ans=v[i].second;

        int count=0;

        for(int i=0;i<n/2;i++){

            if(abs(nums[i]-nums[n-1-i])==ans) continue;

            else{

                if(abs(nums[i]-k)>=ans || abs(nums[i]-0)>=ans ||abs(nums[n-1-i]-k)>=ans || abs(nums[n-1-i]-0)>=ans ){

                    count++;
                }

                else count+=2;
            }
        }

        val=min(val,count);

      }

        return val;
    }
};
