class Solution {
    public int minChanges(int[] nums, int k) {
        int n=nums.length;
        int res=0;
        Map<Integer,Integer> map=new HashMap<>();
        for(int i=0;i<n;i++){
            int a=Math.abs(nums[i] - nums[n - i - 1]);
            map.put(a,map.getOrDefault(a,0)+1);
        }
        int max=-1;
        int value=-1;
        int max_2=-1;
        int value_2=-1;
        for(Map.Entry<Integer, Integer> entry : map.entrySet()){
            if(entry.getValue()>max || (entry.getValue()==max && entry.getKey()<value)){
                max=entry.getValue();
                value=entry.getKey();
            }
        }
        for(Map.Entry<Integer, Integer> entry : map.entrySet()){
            if(entry.getValue()>max_2 && entry.getValue()!=max){
                max_2=entry.getValue();
                value_2=entry.getKey();
            }
        }
        int d=0;
        for(int i=0;i<n;i++){
            if(Math.abs(nums[i] - nums[n - i - 1])!=value){
                if(Math.min(nums[i],nums[n - i - 1])+value>k && Math.max(nums[i],nums[n - i - 1])-value<0)
                    d++;
            }
        }
        int d_2=0;
        for(int i=0;i<n;i++){
            if(Math.abs(nums[i] - nums[n - i - 1])!=value_2){
                if(Math.min(nums[i],nums[n - i - 1])+value_2>k && Math.max(nums[i],nums[n - i - 1])-value_2<0)
                    d_2++;
            }
        }
        return Math.min((n-max)/2+d/2,(n-max_2)/2+d_2/2);
    }
}
