class Solution {
public:


struct BIT
{
	int N;
	vector<int> bit;

	void init(int n)
	{
		N = n;
		bit.assign(n + 1, 0);
	}

	void update(int idx, int val)
	{
		idx++;
		while(idx <= N)
		{
			bit[idx] += val;
			// bit[idx] %= mod;
			idx += idx & -idx;
		}
	}



	int pref(int idx)
	{
		idx++;
		int ans = 0;
		while(idx > 0)
		{
			ans += bit[idx];
			idx -= idx & -idx;
		}
		// ans %= mod;
		return ans;
	}

	int rsum(int l, int r)
	{
        l++, r++;
		return pref(r) - pref(l - 1);
	}

};



    int minChanges(vector<int>& nums, int k) {


        map<int, vector<pair<int,int>>> m;

        int n = nums.size();

        for(int i=0;i<n/2;i++){
            m[abs(nums[i]- nums[n-i-1])].push_back({abs(nums[i]- nums[n-i-1]), max(abs(0- max(nums[i], nums[n-i-1])), abs(k- min(nums[i], nums[n-i-1])))});
        }

        vector<int> v1, v;
        map<int,int> frr;

        for(auto it: m){
            for(auto i: it.second){
                v1.push_back(i.first);
                v.push_back(i.second);
            }
        }


        BIT tree;
        tree.init(k+ 5);

        for(int i=0;i<v.size();i++){
            tree.update(v[i], 1);
        }

        int ans = 1e9;

        for(int i=v.size()-1;i>= 0;i--){

            tree.update(v[i], -1);

            int cnt= n/2;
            cnt -= int(m[v1[i]].size());
            cnt += tree.pref(v1[i]- 1);

            ans = min(ans, cnt);
        }

        return ans;


    }
};
