class Solution {
 public:
  using PII = pair<int, int>;
  class BIT {
   private:
    vector<int> nums;
    int n;

   public:
    BIT(int n) : n(n) { nums.resize(n + 1); }
    int lowbit(int i) { return i & -i; }
    void update(int i, int val) {
      for (; i <= n; i += lowbit(i)) {
        nums[i] += val;
      }
    }
    int query(int i) {
      int ans = 0;
      for (; i >= 1; i -= lowbit(i)) {
        ans += nums[i];
      }
      return ans;
    }
    int sum(int l, int r) { return query(r) - query(l - 1); }
  };
  int minChanges(vector<int>& nums, int k) {
    int n = nums.size();
    int m = n / 2;
    vector<PII> diff(m);
    unordered_map<int, int> cnt;
    for (int i = 0; i < m; i++) {
      if (nums[i] > nums[n - i - 1]) swap(nums[i], nums[n - i - 1]);
      diff[i] = {nums[n - i - 1] - nums[i], max(k - nums[i], nums[n - i - 1])};
      cnt[diff[i].first]++;
    }
    sort(diff.begin(), diff.end());
    BIT bit(k + 2);
    int ans = m;
    int maxd = INT_MIN;
    for (int i = 0; i < m; i++) {
      int d = diff[i].first;
      int md = diff[i].second;
      int lt = bit.query(d);
      ans = min(ans, m - cnt[d] + lt);
      bit.update(md + 1, 1);
    }
    return ans;
  }
};
