class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int n2 = n / 2;
        vector<int> ps;
        int m = k;
        auto &a = nums;
        for (int i = 0, t; i < n2; i++) {
            t = abs(a[i] - a[n - i - 1]);
            m = max(t, m);
            m = max(max(a[i], a[n - i - 1]), m);
            m = max(max(k - a[i], k - a[n - i - 1]), m);
        }
        ps.resize(m + 3);
        ps[0] = -n;
        int mn;
        for (int i = 0, f, g, l, r; i < n2; i++) {
            f = a[i], g = a[n - i - 1];
            mn = min(f, g);
            if (mn > k) {
                l = mn - k;
            } else {
                l = 0;
            }
            r = max(max(f, g), max(k - f, k - g));
            ps[l]++;
            ps[r + 1]--;
            mn = abs(f - g);
            ps[mn]++;
            ps[mn + 1]--;
            if (mn > r) {
                ps[mn]++;
                ps[mn + 1]--;
            }
        }
        int res = 1e9;
        for (int i = 0, l = ps.size(); i + 1 < l; i++) {
            ps[i + 1] += ps[i];
            res = min(res, -ps[i]);
            //cout << ps[i] << ' ';
        }
        //cout << endl;
        return res;
    }
};
