#include <ext/pb_ds/assoc_container.hpp>
#include <ext/pb_ds/tree_policy.hpp>

using namespace __gnu_pbds;

typedef tree<
    int,
    null_type,
    less_equal<int>,
    rb_tree_tag,
    tree_order_statistics_node_update>
    ordered_set;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), m = n/2;

        ordered_set st;

        unordered_map<int, int> mp;


        for(int i=0; i<m; i++) {
            int j = n-i-1;

            int s = min(nums[i], nums[j]);
            int m = max(nums[i], nums[j]);


            int val1 = max(abs(s - k), s);
            int val2 = max(abs(m - k), m);

            st.insert(max(val1, val2));
            mp[abs(nums[j] - nums[i])]++;

            // cout<<i<<" "<<max(val1, val2)<<endl;
        }
        // cout<<endl;

        int ans = INT_MAX;

        for(int i=0; i<=k; i++) {
            int cnt1 = st.order_of_key(i); // requires 2 op
            int cnt2 = m - cnt1;    // requires 1 op

            cnt2 -= mp[i];    // already existing

            ans = min(ans, 2*cnt1 + cnt2);

            // cout<<i<<" "<<cnt1<<" "<<cnt2<<endl;
        }

        return ans;
    }
};
