#define rep(i,a,n) for(int i=a;i<=n;i++)
#define per(i,a,n) for(int i=n;i>=a;i--)
#define pb push_back
#define SZ(v) ((int)v.size())
#define fs first
#define sc second
#define all(x) (x.begin()),(x.end())
typedef long long ll;
typedef double db;
typedef pair<int,int> pii;
class Solution {
public:
    int minChanges(vector<int>& a, int k) {
    	int n=SZ(a);
        vector<int> s(max(n,k)+10,0);
        rep(i,0,n/2-1){
        	int d=abs(a[i]-a[n-1-i]);
        	s[d]--;s[d+1]++;
        	int c1=max(max(a[i],a[n-1-i]),k-min(a[i],a[n-1-i]));
        	s[0]++;s[c1+1]--;
        	s[c1+1]+=2;
		}
		int ans=s[0];
		rep(i,1,max(n,k)+5){
			s[i]+=s[i-1];
            // cout<<i<<" "<<s[i]<<endl;
			ans=min(ans,s[i]);
		}
		return ans;
    }
};
